mapper.select:
		SELECT * FROM
            (
            SELECT ROWNUM AS RN, tbl.* FROM
                (
                SELECT * FROM tbl_reply
                WHERE bno = #{boardNo}
                ORDER BY reply_no DESC
                ) tbl
            )

        WHERE rn > #{paging.pageStart} AND rn <= #{paging.pageEnd}

SELECT COUNT(*)
FROM tbl_reply
WHERE bno = #{bno}
